// fetching JSON files

d3.json("data/nobel_winners.json").then((data) => {
  console.log(`Dataset: ${data}`);
  makeChart(data);
});

function makeChart(data) {
  let cat_group = d3.rollup(
    data,
    (v) => v.length,
    (d) => d.gender,
    (d) => d.category
  );

  let male = cat_group.get("male");
  let female = cat_group.get("female");
  let categories = [...male.keys()].sort();

  let traceM = {
    y: categories,
    x: categories.map((c) => male.get(c)),
    name: "male prize total",
    type: "bar",
    orientation: "h",
  };
  let traceF = {
    y: categories,
    x: categories.map((c) => female.get(c)),
    name: "female prize total",
    type: "bar",
    orientation: "h",
  };

  let traces = [traceM, traceF];
  let layout = { bardmode: "group", margin: { l: 160 } };

  Plotly.newPlot("gender-category", traces, layout);
}

// user-driven plotly with javascript and html

window.d3 = d3;
let info;
d3.json("data/nobel_winners.json").then((_info) => {
  console.log(_info);
  info = _info;
  updateChart();
});

let selectedGroup = "gender";

function updateChart() {
  let traces = [
    {
      type: "violin",
      x: info.map((d) => d[selectedGroup]),
      y: info.map((d) => d.award_age),
      points: "none",
      box: { visible: true },
      line: { color: "green" },
      meanline: { visible: true },
    },
  ];

  let layout = {
    title: "Age distributions of the Nobel Prizewinners",
    yaxis: {
      zeroline: false,
    },
    xaxis: {
      categoryorder: "category ascending",
    },
  };

  // it is react not update
  Plotly.react("violin-group", traces, layout);
}

let availableGroups = ["gender", "category"];
availableGroups.forEach((g) => {
  d3.select("#nobel-group")
    .append("option")
    .property("selected", g === selectedGroup)
    .attr("value", g)
    .text(g);
});

d3.select("#nobel-group").on("change", function (e) {
  selectedGroup = d3.select(this).property("value");
  updateChart();
});
